mapper.select:
SELECT

FROM MistakeQuestion
WHERE user_id = #{userId,jdbcType=VARCHAR} AND question_id = #{questionId,jdbcType=VARCHAR}

mapper.update:
UPDATE MistakeQuestion
SET other_reason = #{otherReasonText,jdbcType=VARCHAR},
    update_time  = NOW()
WHERE user_id = #{userId,jdbcType=VARCHAR}
  AND question_id = #{questionId,jdbcType=VARCHAR}

UPDATE MistakeQuestion
SET study_note  = #{studyNote,jdbcType=LONGVARCHAR},
    update_time = NOW()
WHERE user_id = #{userId,jdbcType=VARCHAR}
  AND question_id = #{questionId,jdbcType=VARCHAR}

mapper.insert:
UPDATE MistakeQuestion
SET knowledge_point_id = #{knowledgePointId,jdbcType=VARCHAR},
    subject            = #{subject,jdbcType=VARCHAR},
    update_time        = NOW()
WHERE user_id = #{userId,jdbcType=VARCHAR}
  AND question_id = #{questionId,jdbcType=VARCHAR}
